import axios from 'axios'

// 动态创建 axios 实例
export function createParticipantApi(port) {
  return axios.create({
    baseURL: `http://localhost:${port}`
  })
}

// 示例：获取自身状态
export function getSelfStatus(port) {
  return createParticipantApi(port).get('/api/participant/status')
}

export function getOnlineStatus(port) {
  return createParticipantApi(port).get('/api/participant/online-status')
}

export function collaborativeDecrypt(port, data) {
  return createParticipantApi(port).post('/api/participant/collaborative/decrypt', data)
}

export function collaborativeRefresh(port, data) {
  return createParticipantApi(port).post('/api/participant/collaborative/refresh', data)
}
